mapper.select:
select *, (select count(*) comment_count from nb_board_${category} where parent = ${category}.no and cm_count = 1) comment_count from nb_board_${category} ${category}
${search} like CONCAT ('%', #{keyword}, '%')
subject like CONCAT ('%', #{keyword}, '%') or content like CONCAT ('%', #{keyword}, '%')
and subject is not null
order by no desc, register_date desc limit #{pageStart}, #{perPageNum};

SELECT * FROM
SELECT * FROM nb_board_${type}
AS t ORDER BY t.register_date DESC limit 5

mapper.insert:
insert into nb_board_${category} (parent, cm_body, cm_password,
	cm_nick, cm_count,ip) values (#{parent},#{cm_body}, #{cm_password},
	#{cm_nick}, 1, #{ip});

update.if:
UPDATE
	nb_board_${category}
SET
	bo_good =  bo_good + 1
WHERE
	no = #{no}

UPDATE
	nb_board_${category}
SET
	bo_bad =  bo_bad + 1
WHERE
	no = #{no}

UPDATE
	nb_board_${category}
SET
	cm_good =  cm_good + 1
WHERE
	no = #{no}

UPDATE
	nb_board_${category}
SET
	cm_bad =  cm_bad + 1
WHERE
	no = #{no}
